\copy (select o.timefrom, o.timeto, (o.timeto - o.timefrom) as timediff, v.name, v.hostname, g.resolution, g.period, g.start_date, g.from as "report start", g.to as "report end" from outages o inner join reports r on o.report_id=r.id inner join vpcs v on r.vpc_id=v.id inner join global_reports g on r.global_report_id=g.id where o.status='down' and (o.timeto - o.timefrom)>'00:03:00' and g.period='month' and g.resolution='day') to './csv/outages.csv' with csv;
